mapper.select:
select
id, dict_type_code, dict_type_name, dict_code, dict_name, area_code, remarks, use_state
from sys_data_dict
where dict_type_code = #{dictTypeCode}
and dict_code = #{dictCode}

    and area_code = #{areaCode}

select
  id, dict_type_code, dict_type_name, dict_code, dict_name, area_code, remarks, use_state
from sys_data_dict
where dict_type_code = #{dictTypeCode}
and dict_code = #{dictCode}

select
  id, dict_type_code, dict_type_name, dict_code, dict_name, area_code, remarks, use_state
from sys_data_dict
where dict_type_code = #{dictTypeCode}
and dict_name = #{dictName}

select
  id, dict_type_code, dict_type_name, dict_code, dict_name, area_code, remarks, use_state
from sys_data_dict


        and dict_type_code = #{dictTypeCode}


        and dict_type_name like "%"#{dictTypeName}"%"


        and dict_name like "%"#{dictName}"%"


        and area_code = #{areaCode}


        and use_state = #{useState}

select
  id, dict_type_code, dict_type_name, dict_code, dict_name, area_code, remarks, use_state
from sys_data_dict


        and id != #{id}


        and dict_type_code = #{dictTypeCode}


        and dict_type_name = #{dictTypeName}


        and dict_code = #{dictCode}


        and dict_name like "%"#{dictName}"%"


        and area_code = #{areaCode}


        and remarks = #{remarks}


        and use_state = #{useState}


order by CONVERT(dict_code,SIGNED) asc
