select n.id, n.sys_code, n.node_code, n.node_name,n.node_show_name, n.para_node_code, n.para_node_name, n.node_url,n.name_path, n.node_type, n.node_icon, n.node_level, n.remarks, n.last_stage, n.use_state,n.button,n.button_name,n.order_no,n.terminal_type,n.link_type,n.node_level_name,n.operating_booklet from sys_node n
n.use_state = 1
and n.sys_code = #{sysCode}
and n.terminal_type = #{terminalType}
